import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { format } from 'date-fns';
import { getAllPosts, searchPosts, getPostsByCategory } from '../data/blogPosts';
import './Home.css';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');

  useEffect(() => {
    const allPosts = getAllPosts();
    setPosts(allPosts);
    setFilteredPosts(allPosts);
  }, []);

  useEffect(() => {
    let filtered = posts;
    
    if (searchTerm) {
      filtered = searchPosts(searchTerm);
    }

    if (selectedCategory !== 'all') {
      filtered = getPostsByCategory(selectedCategory);
    }

    if (searchTerm && selectedCategory !== 'all') {
      const searchResults = searchPosts(searchTerm);
      filtered = searchResults.filter(post => post.category === selectedCategory);
    }

    setFilteredPosts(filtered);
  }, [searchTerm, selectedCategory, posts]);

  const categories = ['all', ...new Set(posts.map(post => post.category))];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const cardVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  return (
    <div className="home-page">
      <motion.div
        className="hero-section"
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <h1 className="hero-title">
          Welcome to <span className="gradient-text">Dynamic Blog</span>
        </h1>
        <p className="hero-subtitle">
          Discover amazing stories, insights, and knowledge shared by our community
        </p>
      </motion.div>

      <motion.div
        className="search-filter-section glass"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, delay: 0.2 }}
      >
        <div className="search-container">
          <input
            type="text"
            placeholder="Search posts..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>

        <div className="filter-container">
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            className="category-filter"
          >
            {categories.map(category => (
              <option key={category} value={category}>
                {category.charAt(0).toUpperCase() + category.slice(1)}
              </option>
            ))}
          </select>
        </div>
      </motion.div>

      <motion.div
        className="posts-grid"
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        {filteredPosts.map((post, index) => (
          <motion.article
            key={post.id}
            className="post-card glass"
            variants={cardVariants}
            whileHover={{ 
              y: -10,
              scale: 1.02,
              transition: { duration: 0.3 }
            }}
          >
            <div className="post-header">
              <div className="post-meta">
                <span className="post-category">{post.category}</span>
                <span className="post-date">{format(post.date, 'MMM dd, yyyy')}</span>
              </div>
              <span className="read-time">{post.readTime}</span>
            </div>

            <Link to={`/post/${post.id}`} className="post-link">
              <h2 className="post-title">{post.title}</h2>
            </Link>

            <p className="post-excerpt">{post.excerpt}</p>

            <div className="post-footer">
              <div className="post-author">
                <span className="author-name">By {post.author}</span>
              </div>
              <div className="post-tags">
                {post.tags.map(tag => (
                  <span key={tag} className="tag">{tag}</span>
                ))}
              </div>
            </div>
          </motion.article>
        ))}
      </motion.div>

      {filteredPosts.length === 0 && (
        <motion.div
          className="no-posts glass"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <h3>No posts found</h3>
          <p>Try adjusting your search terms or category filter.</p>
        </motion.div>
      )}
    </div>
  );
};

export default Home;
